import angular from 'angular';
const app = angular.module('app');
app.service('$restService', function ($http) {
  this.query = (model, query) => $http.get(`/api/rest/${ model }/query`, { params: query });
  this.create = (model, option) => $http.post(`/api/rest/${ model }`, option);
  this.get = (model, id, query) => $http.get(`/api/rest/${ model }/${ id }`, { params: query });
  this.modify = (model, id, option) => $http.put(`/api/rest/${ model }/${ id }`, option);
  this.del = (model, id, query) => $http.delete(`/api/rest/${ model }/${ id }`, { params: query });
});